const LoadedFeed = (props) => {
    const responseObj = props.responseObj;

    // If no such account found
    if (responseObj.message) {
        return (
            <div className="error-page">
                <h1>No such account found !</h1>
                <p>{responseObj.message}</p>
            </div>
        );
    }
    const storyArray = responseObj.feed.data;

    // If account exists but no story uploaded
    if (!storyArray.length) {
        return (
            <>
               
                <div className="error-page">
                    <h1>No story found !</h1>
                    <p>User has not uploaded any story !</p>
                </div>
            </>
        );
    }

    // When User found and story is uploaded
    return (
        <>
           <article className="profile-description">
                <div className="profile-header">
                    <img
                        src={responseObj.profile_pic_url}
                        className="profile-picture"
                        alt="Profile"
                    />
                    <section className="profile-counts">
                        <div className="media-count">
                            <h3>{responseObj.media_count}</h3>
                            <p>Posts</p>
                        </div>
                        <div className="follower-count">
                            <h3>{responseObj.follower_count}</h3>
                            <p>Followers</p>
                        </div>
                        <div className="following-count">
                            <h3>{responseObj.following_count}</h3>
                            <p>Followings</p>
                        </div>
                    </section>
                </div>
            </article>
            <section className="user-intro">
                <h3 className="username">{responseObj.username}</h3>
                <p className="fullname">{responseObj.full_name}</p>
                <p className="bio">{responseObj.biography}</p>
            </section>
            <section className="story-list">
                {storyArray.map((element, index) => {
                    if (element.media_type === 2) {
                        return (
                            <>
                             <div className="story-items">
                            <video className="story-item" key={index} controls>
                                <source
                                    src={element.video_versions[0].url}
                                    className="story-media"
                                    type="video/webm"
                                ></source>
                                
                            </video>
                          
                            </div>
                            <h3>{element.caption.text}</h3>
                            </>
                        );
                    } if(element.media_type === 1) {
                        return (
                            <div className="story-item" key={index}>
                                <img
                                    src={
                                        element.image_versions2.candidates[0].url
                                    }
                                    className="story-media"
                                    alt=""
                                />
                                 <h3>{element.caption.text}</h3>
                            </div>
                        );
                    }
                    if(element.media_type === 8) {
                        return (
                            <div className="story-item" key={index}>
                                <img
                                    src={
                                        element.carousel_media[0].image_versions2.candidates[0].url
                                    }
                                    className="story-media"
                                    alt=""
                                />
                                 <h3>{element.caption.text}</h3>
                            </div>
                        );
                    }
                })}
            </section>
        </>
    );
};

export default LoadedFeed;
