import { useState } from "react";
import "./Styles/reel.css";
import styled from "styled-components";
import LoadingAnimation from "./LoadingAnimation";
import Footer from "./Footer/Footer";
import SearchForm from "./Header/SearchForm";
import LoadedReels from "./LoadedResponses/LoadedReels";
import { device } from "./device";
const Reels = (props) => {
    const [reelUrl, setReelUrl] = useState();
    const [isLoading, setIsLoading] = useState(false);
    const [hasLoaded, setHasLoaded] = useState(false);
    const [responseObj, setResponseObj] = useState();
    const [isOpen, setIsOpen] = useState(false);
    function getId(url) {
        return url.slice(31, 42);
    }

    function onChangehandler(e) {
        setReelUrl(e.target.value);
    }

    function submitHandler(e) {
        e.preventDefault();
        if (reelUrl.length === 0) {
            alert("Empty Reel link !");
            return;
        }
        setIsLoading(true);
        fetchReel();
    }

    async function fetchReel() {
        const reelId = getId(reelUrl);
        if (reelId.length !== 11) {
            alert("Invaid Reel URL");
            setIsLoading(false);
            return;
        }
        const response = await fetch(
            `https://instagram-bulk-profile-scrapper.p.rapidapi.com/clients/api/ig/media_by_id?shortcode=${reelId}&response_type=reels&corsEnabled=true`,
            {
                method: "GET",
                headers: {
                    "x-rapidapi-key": "d57e68a8eamsh96aa54d1038b6ffp17cb2djsn17a540de17a9",
                    "x-rapidapi-host": "instagram-bulk-profile-scrapper.p.rapidapi.com",
                },
            }
        );
        const data = await response.json();
        setResponseObj(data[0]);
        setIsLoading(false);
        setHasLoaded(true);
    }

    return (
        <>

<Container >
<Nav>
      <Logo href="">
        Instait<span style={{fontSize:'12px'}}>&nbsp;&nbsp;&nbsp;-By Yasin</span>
      </Logo>
      <Hamburger onClick={() => setIsOpen(!isOpen)}>
        <span />
        <span />
        <span />
      </Hamburger>
      <Menu isOpen={isOpen}>

        <MenuLink href="https://fbit.herokuapp.com/">Facebook Downloader</MenuLink>
        <MenuLink href="https://utit.herokuapp.com/">Youtube Downloader</MenuLink>
        <MenuLink href="https://downitt.herokuapp.com/">DailyMotion/Adult site Downloader</MenuLink>
        <MenuLink href="https://scdit.herokuapp.com/">Soundcloud Downloader</MenuLink>

      </Menu>
    </Nav>

</Container>
        <section id="reel-section">
            <SearchForm
                submitHandler={submitHandler}
                onChangehandler={onChangehandler}
                placeholder="Enter Reel link"
            />

            <div className="reel-response">
                {isLoading && <LoadingAnimation />}
                {hasLoaded && <LoadedReels responseObj={responseObj} />}
            </div>
        </section>
        <Head>Reel features</Head>
  <CardWrapper>

  <Image src={`https://igram.io/static/img/app-instagram-feed.jpg`} />
  <Content>
  <Heading>Photos Downloader</Heading>
  <Para>
  A free and fast tool for Instagram reels video download in HD. With this Instagram reels downloader, you can save reels video in MP4 high quality in your phone gallery without providing your log in details. The Instagram reels downloader is a free service for everyone and you can unlimitedly download reels video as you want.</Para>
  </Content>

</CardWrapper>
<CardWrapper>
  <Image src={`https://igram.io/static/img/instagram-video.jpg`} />
  <Content>
  <Heading>Videos Downloader</Heading>
  <Para>
  Instait is created to enable you to download IG
   videos for any purpose you want. Instait supports video downloading for
    singular video and multiple video from carousels.</Para>
  </Content>

</CardWrapper>
<CardWrapper>
  <Image src={`https://igram.io/static/img/applications-instagram-igtv.jpg`} />
  <Content>
  <Heading>IGTV Downloader</Heading>
  <Para>
  IGTV is a long video type, in case you can’t watch it now, you can download IGTV videos
   to your device, to be sure that you can return to watching later, without need to be online
    or in case the IGTV can be deleted..</Para>
  </Content>

</CardWrapper>
<CardWrapper>
  <Image src={`https://igram.io/static/img/instagram-reels.jpg`} />
  <Content>
  <Heading>Reel Downloader</Heading>
  <Para>
  REEL is a new video format that clone the principle of TikTok.
   Download Instagram REEL videos with help of Instait. Our REEL downloader can help you to save your favorite Reels videos.</Para>
  </Content>

</CardWrapper>



 </>
    );
};

const MenuLink = styled.a`
  padding: 1rem 2rem;
  cursor: pointer;

  text-align: center;
  text-decoration: none;
  color: #67bc98;
  transition: all 0.3s ease-in;
  font-size: 0.9rem;
  &:hover {
    background: linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%); 
-webkit-background-clip: text;
-webkit-text-fill-color: transparent;
  }
`;

const Nav = styled.div`
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  background: white;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
`;

const Logo = styled.a`
  padding: 1rem 0;
  color: #7b7fda;
  text-decoration: none;
  font-weight: 800;
  font-size: 1.7rem;
  span {
    font-weight: 300;
    font-size: 1.3rem;
  }
`;

const Menu = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  @media (max-width: 768px) {
    overflow: hidden;
    flex-direction: column;
    max-height: ${({ isOpen }) => (isOpen ? "300px" : "0")};
    transition: max-height 0.3s ease-in;
    width: 100%;
  }
`;

const Hamburger = styled.div`
  display: none;
  flex-direction: column;
  cursor: pointer;
  span {
    height: 2px;
    width: 25px;
    background: #7b7fda;
    margin-bottom: 4px;
    border-radius: 5px;
  }
  @media (max-width: 768px) {
    display: flex;
  }
`;

const Head = styled.div`
font-size: 50px;
text-align: center;
margin-top: 10%;
font-weight: bold;
border-top: 1px solid #000;
background: linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%); 
-webkit-background-clip: text;
-webkit-text-fill-color: transparent;
`



const Container = styled.div`
background-color:#0d1117;

display:grid;
place-items:center;


`;
const CardWrapper = styled.div`
  display: flex;
  flex-direction: column;
  border: 1px solid gray;
  box-shadow: 5px 5px #ccc;
  padding: 10px;
  margin: 10px;
  @media ${device.laptop} {
    flex-direction: row;
  }
`;

const Image = styled.img`
max-width: 600px !important;
  height: auto;
`;

const Content = styled.div`padding: 10px;

`;

const Heading = styled.div`
font-size: 40px;
font-weight: bolder;
text-align: center

`
const Para = styled.div`
font-size: 20px;
letter-spacing: 2px;
margin-top: 30px !important;
padding: 40px;
background: linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%); 
-webkit-background-clip: text;
-webkit-text-fill-color: transparent;   

text-shadow: 2px 0 #e6683c;
`




export default Reels;
